changeSet.sql:
-- Set created_by for null values
UPDATE category SET created_by = 'system' WHERE created_by IS NULL;

-- Set created_by for null values
UPDATE category SET created_date = current_timestamp WHERE created_date IS NULL;

-- Set last_modified_by for all rows
UPDATE category SET last_modified_by = 'system' WHERE last_modified_by IS NULL;

-- Set last_modified_by for all rows
UPDATE category SET last_modified_date = current_timestamp WHERE last_modified_date IS NULL;
